/** File per la gestione del mapping dei core di SOLR **/
             
exports.getCore = function (city) {
    
   switch(city){
       case 'bergamo'   : return "localbergamo";
       case 'bologna'   : return "localbologna";
       case 'brescia'   : return "localbrescia";
       case 'firenze'   : return "corfiorentino";
       case 'milano'    : return "localmilano";
       case 'roma'      : return "localroma";
       case 'bari'      : return "localcormez";
       case 'caserta'   : return "localcormez";
       case 'catania'   : return "localcormez";
       case 'foggia'    : return "localcormez";
       case 'lecce'     : return "localcormez";
       case 'napoli'    : return "localcormez";
       case 'palermo'   : return "localcormez";
       case 'salerno'   : return "localcormez";
       case 'belluno'   : return "localveneto";
       case 'padova'    : return "localveneto";
       case 'rovigo'    : return "localveneto";
       case 'treviso'   : return "localveneto";
       case 'veneto'    : return "localveneto";
       case 'venezia'   : return "localveneto";
       case 'verona'    : return "localveneto";
       case 'vicenza'   : return "localveneto";
       default: return "notfound";
   }
    
}             
             
