//modulo simple_module
var solrHost = "http://solr-slaves.farm-rcs.it/solr_repo/gzPrimiPiani/select/";

var solrOptions = "&version=2.2&start=0&rows=10&indent=on";

var chiamaSolrText = new function (queryText){
	console.log("queryText");
		var urlToCall = solrHost+"?q="+encodeURIComponent(queryText)+solrOptions;
		
		$.ajax({
			method: "GET",
			url: urlToCall,
		})
		.done(function(callResponse){
			return callResponse;
		})		
		
}

export.chiamaSolrText = chiamaSolrText;